// Renderizar template para TODOS los usuarios del array
const items = $input.all();
const template = $('Obtener Template Bienvenida').first().json.html_template;

// Determinar base_url segÃºn entorno
const baseUrl = process.env.WEBHOOK_URL || 'http://localhost:5678';

console.log('ðŸ“§ Procesando', items.length, 'usuarios');

// Procesar cada usuario
const results = items.map(item => {
  const usuario = item.json;
  const nivel = usuario.nivel_actual === 'iniciacion' ? 'IniciaciÃ³n' : 'Intermedio';

  console.log('  â†’ Renderizando email para:', usuario.email);

  const html = template
    .replace(/\{\{nombre\}\}/g, usuario.nombre || '')
    .replace(/\{\{nivel\}\}/g, nivel)
    .replace(/\{\{user_id\}\}/g, usuario.id)
    .replace(/\{\{base_url\}\}/g, baseUrl);

  return {
    json: {
      html_renderizado: html,
      email: usuario.email,
      nombre: usuario.nombre,
      user_id: usuario.id
    }
  };
});

return results;

