// Combinar query params con config
const webhookData = $('Webhook Seleccionar Sesiones').first().json;
const config = $('Obtener Config').first().json.config;

return {
  json: {
    query: webhookData.query,
    config: config
  }
};

// Añadir config al resultado del usuario
const userData = $json;
const configData = $('Combinar Config').first().json.config;

return {
  json: {
    ...userData,
    config: configData
  }
};

// Renderizar template de procesando con datos del usuario
const template = $json.html_template;
const userData = $node['Añadir Config a Usuario'].json;

const renderedHTML = template
  .replace(/\{\{sesiones_objetivo_semana\}\}/g, userData.sesiones_objetivo_semana || '');

return {
  json: {
    html_rendered: renderedHTML,
    userData: userData
  }
};

// Preparar email usando template de BD (dark mode unificado)

const usuario = $node["Añadir Config a Usuario"].json;
const sesion = $node["Obtener Sesión Completa"].json;
const template = $node["Obtener Template Email"].json.html_template;
const config = usuario.config;

console.log('Preparando email con template de BD');
console.log('Sesión ID:', sesion.sesion_id);

const webhookUrl = config.WEBHOOK_URL || 'http://localhost:5678';
const sesionUrl = `${webhookUrl}/webhook/view-session/sesion/${sesion.sesion_id}?token=${usuario.auth_token}`;

// Calcular contenido según tipo de sesión
let totalEjercicios = 'N/A';
let enfoqueTexto = sesion.enfoque || 'Mixto';

if (sesion.enfoque === 'fuerza' && sesion.calentamiento && sesion.trabajo_principal) {
  const calentamiento = typeof sesion.calentamiento === 'string' ? JSON.parse(sesion.calentamiento) : sesion.calentamiento;
  const trabajoPrincipal = typeof sesion.trabajo_principal === 'string' ? JSON.parse(sesion.trabajo_principal) : sesion.trabajo_principal;
  totalEjercicios = `${(calentamiento?.length || 0) + (trabajoPrincipal?.length || 0)} ejercicios personalizados`;
  enfoqueTexto = 'Fuerza Funcional';
} else if (sesion.enfoque === 'cardio') {
  totalEjercicios = 'Actividad cardiovascular guiada';
  enfoqueTexto = 'Cardio y Resistencia';
}

// Reemplazar variables en template de BD
const emailHTML = template
  .replace(/\{\{nombre\}\}/g, usuario.nombre || '')
  .replace(/\{\{sesiones_objetivo\}\}/g, usuario.sesiones_objetivo_semana || '')
  .replace(/\{\{introduccion\}\}/g, sesion.introduccion_personalizada || 'Sesión diseñada para tu nivel y objetivos.')
  .replace(/\{\{duracion\}\}/g, sesion.duracion_estimada || '25-30 minutos')
  .replace(/\{\{enfoque\}\}/g, enfoqueTexto)
  .replace(/\{\{contenido\}\}/g, totalEjercicios)
  .replace(/\{\{nivel\}\}/g, usuario.nivel_actual || '')
  .replace(/\{\{sesion_url\}\}/g, sesionUrl);

return {
  json: {
    user_id: usuario.id,
    user_email: usuario.email,
    user_nombre: usuario.nombre,
    sesion_id: sesion.sesion_id,
    sesiones_objetivo: usuario.sesiones_objetivo_semana,
    semana: usuario.semana_actual,
    sesion_numero: 1,
    email_html: emailHTML,
    asunto: `Tu Sesión 1 de ${usuario.sesiones_objetivo_semana}: ${sesion.titulo}`,
    webhook_url: webhookUrl
  }
};
