// Extraer datos del pago desde el Router
const input = $input.first().json;
const body = input.body;

console.log('üéØ [ONBOARDING 89‚Ç¨] Procesando pago...');

const session = body.data?.object;
const customerDetails = session?.customer_details;

let customerEmail = customerDetails?.email;
let customerName = customerDetails?.name || '';
let amountPaid = (session?.amount_total || 0) / 100;
let stripeCustomerId = session?.customer;

console.log('üìß Email:', customerEmail);
console.log('üí∞ Monto:', amountPaid);

if (!customerEmail) {
  console.log('‚ö†Ô∏è No se pudo extraer email, ignorando evento');
  return [];
}

return {
  json: {
    email: customerEmail,
    nombre: customerName,
    monto_pagado: amountPaid,
    stripe_customer_id: stripeCustomerId,
    fecha_pago: new Date().toISOString()
  }
};

// Formatear datos para asegurar propagaci√≥n correcta
const input = $input.first().json;

console.log('üîÑ Formateando datos de usuario para propagaci√≥n...');

return {
  json: {
    id: input.id,
    email: input.email,
    nombre: input.nombre,
    nivel_actual: input.nivel_actual,
    etapa: input.etapa,
    semana_actual: input.semana_actual,
    fecha_inicio: input.fecha_inicio,
    estado: input.estado
  }
};

// Usuario ya estaba activo - ignorar
console.log('‚è≠Ô∏è Usuario ya estaba activo, ignorando evento duplicado');
return { json: { status: 'already_active' } };

// Recuperar datos del usuario despu√©s de actualizar Brevo
const datosUsuario = $node["Formatear Datos Usuario"].json;

console.log('üîÑ Recuperando datos de usuario para enviar email...');

return {
  json: datosUsuario
};

// Renderizar template con variables del usuario
const template = $json.html_template;
const usuario = $('Recuperar Datos Usuario').first().json;

console.log('üîÑ Renderizando template bienvenida para:', usuario.email);

// Determinar base_url seg√∫n entorno
const baseUrl = 'http://localhost:5678';

// Formatear nivel para mostrar
const nivelDisplay = usuario.nivel_actual === 'iniciacion' ? 'Iniciaci√≥n' : 'Intermedio';

// Reemplazar variables
const html = template
  .replace(/\{\{nombre\}\}/g, usuario.nombre || '')
  .replace(/\{\{user_id\}\}/g, usuario.id || '')
  .replace(/\{\{nivel\}\}/g, nivelDisplay)
  .replace(/\{\{base_url\}\}/g, baseUrl);

return {
  json: {
    html_renderizado: html,
    email: usuario.email,
    nombre: usuario.nombre
  }
};

