// Extraer datos del cuestionario
const body = $input.first().json.body;

// Parsear si viene como string
const data = typeof body === 'string' ? JSON.parse(body) : body;

return {
  json: {
    email: data.email,
    nombre: data.nombre,
    tiempo_sin_ejercicio: data.tiempo_sin_ejercicio,
    nivel_movilidad: data.nivel_movilidad,
    limitaciones: data.limitaciones,
    limitaciones_detalle: data.limitaciones_detalle || '',
    objetivo_principal: data.objetivo_principal,
    nivel_asignado: data.nivel_asignado,
    duracion_programa: data.duracion_programa,
    timestamp: data.timestamp
  }
};

