// Preparar email de programa completado

function replaceVariables(template, variables) {
  let result = template;
  for (const [key, value] of Object.entries(variables)) {
    const safeValue = String(value ?? '');
    const regex = new RegExp(`\\{\\{${key}\\}\\}`, 'g');
    result = result.replace(regex, safeValue);
  }
  return result;
}

// Usuario actual (del nodo anterior en el loop)
const usuario = $json;
// Template obtenido al inicio del workflow
const template = $('Obtener Templates').first().json.template_completado;

console.log('ðŸ† Preparando email de programa completado para:', usuario.email);

const variables = {
  nombre: usuario.nombre,
  nivel_actual: usuario.nivel_actual,
  intensidad: usuario.intensidad_nivel
};

const emailHTML = replaceVariables(template, variables);

return {
  json: {
    user_id: usuario.id,
