// Preparar body para Brevo - CENTRALIZADO
// Entrada esperada:
// - user_email: string
// - user_nombre: string
// - asunto: string
// - email_html: string
// - user_id: number (opcional, para registrar envÃ­o)
// - sesion_id: number (opcional)

const input = $('Webhook Enviar Email').first().json.body || $('Webhook Enviar Email').first().json;

// Obtener config desde BD
const configItems = $('Obtener Config').all();
const config = {};
configItems.forEach(item => {
  config[item.json.key] = item.json.value;
});

const senderName = config.SENDER_NAME || 'Camino Vital';
const senderEmail = config.SENDER_EMAIL || 'hola@habitos-vitales.com';
const brevoApiKey = config.BREVO_API_KEY;

const brevoBody = {
  sender: {
    name: senderName,
    email: senderEmail
  },
  to: [
    {
      email: input.user_email,
      name: input.user_nombre || input.user_email
    }
  ],
  subject: input.asunto || input.email_asunto,
  htmlContent: input.email_html
};

console.log('ðŸ“§ Preparando email para:', input.user_email);
console.log('ðŸ“‹ Asunto:', brevoBody.subject);

return {
  json: {
    ...input,
    brevo_body: brevoBody,
    brevo_api_key: brevoApiKey
  }
};

