  // Formato: approve_clip_{videoId}_{projectId}
  result.clipId = parts[2];
  result.projectId = parts[3];
  result.type = 'individual';
} else {
  // Formato: approve_all_{projectId}
  result.projectId = parts[2];
  result.type = 'all';
}

return { json: result };

