const usuarios = $input.all();

if (usuarios.length === 0) {
  throw new Error('Token inv√°lido o expirado');
}

const usuario = usuarios[0].json;
const sesionId = $node["Webhook Mostrar Sesi√≥n"].json.params.sesion_id;

console.log('‚úÖ Token v√°lido para usuario:', usuario.nombre);

return {
  json: {
    user_id: usuario.user_id,
    user_nombre: usuario.nombre,
    user_email: usuario.email,
    sesion_id: sesionId,
    params: {
      sesion_id: sesionId
    }
  }
};

// Obtener datos de sesi√≥n y templates
const sesion = $node["Obtener Sesi√≥n"].json;
const templatesData = $input.all();

if (!sesion || !sesion.id) {
  throw new Error('Sesi√≥n no encontrada');
}

// Organizar templates por nombre
const templates = {};
templatesData.forEach(t => {
  templates[t.json.nombre] = t.json.html_template;
});

const cssBase = templates['css_base_habitos_vitales'] || '';
const templatePagina = templates['pagina_sesion'] || '';

console.log('üìã Generando p√°gina de sesi√≥n...');
console.log('üéØ Tipo de sesi√≥n:', sesion.enfoque);

// Determinar si es sesi√≥n de FUERZA o CARDIO
const esCardio = sesion.enfoque === 'cardio';

// URLs
const webhookUrl = process.env.WEBHOOK_URL || 'http://localhost:5678';
const feedbackProblemasUrl = `${webhookUrl}/webhook/feedback-problemas?user_id=${sesion.user_id}&sesion=${sesion.numero_sesion}`;

// URL base de Firebase Storage
const firebaseBaseUrl = 'https://firebasestorage.googleapis.com/v0/b/playfit-21e92.appspot.com/o/video%2F';
const firebaseToken = '?alt=media';

let htmlCalentamiento = '';
let htmlTrabajoPrincipal = '';

if (esCardio) {
  // ========== SESI√ìN CARDIO ==========
  console.log('‚ù§Ô∏è Generando HTML para sesi√≥n CARDIO');

  // Calentamiento es texto para cardio
  htmlCalentamiento = `
    <div class="ejercicio ejercicio-calentamiento">
      <div class="ejercicio-header">
        <div class="ejercicio-numero ejercicio-numero-calentamiento">üî•</div>
        <h3>Calentamiento</h3>
      </div>
      <div class="ejercicio-detalles" style="white-space: pre-line;">
        ${sesion.calentamiento_texto || 'Realiza un calentamiento suave de 5 minutos.'}
      </div>
    </div>
  `;

  // Actividad principal para cardio
  const actividad = typeof sesion.actividad_principal === 'string'
    ? JSON.parse(sesion.actividad_principal)
    : sesion.actividad_principal;

  const iconosActividad = {
    'bicicleta': 'üö¥',
    'caminar': 'üö∂',
    'nadar': 'üèä',
    'eliptica': 'üèÉ'
  };
  const iconoActividad = iconosActividad[sesion.tipo_actividad] || 'üèÉ';

  htmlTrabajoPrincipal = `
    <div class="ejercicio ejercicio-principal">
      <div class="ejercicio-header">
        <div class="ejercicio-numero ejercicio-numero-principal">${iconoActividad}</div>
        <h3>${sesion.tipo_actividad ? sesion.tipo_actividad.charAt(0).toUpperCase() + sesion.tipo_actividad.slice(1) : 'Cardio'}</h3>
      </div>
      <div class="ejercicio-detalles">
        <div><strong>‚è±Ô∏è Duraci√≥n:</strong> ${actividad?.duracion_total || sesion.duracion_estimada}</div>
        <div><strong>üíì Intensidad:</strong> ${actividad?.intensidad || 'Moderada'}</div>
        ${actividad?.estructura ? `
        <div style="margin-top: 15px;"><strong>üìã Estructura:</strong></div>
        <div style="white-space: pre-line; margin-top: 10px; padding: 15px; background: rgba(255,255,255,0.04); border-radius: 8px;">${actividad.estructura}</div>
        ` : ''}
      </div>
    </div>

    ${sesion.senales_de_alerta && sesion.senales_de_alerta.length > 0 ? `
    <div class="hv-card hv-card-accent" style="margin-top: 25px;">
      <h4 style="color: #E74C3C; margin-bottom: 15px;">‚ö†Ô∏è Se√±ales de alerta</h4>
      <ul style="color: #B5B5B5; padding-left: 20px;">
        ${sesion.senales_de_alerta.map(s => `<li>${s}</li>`).join('')}
      </ul>
    </div>
    ` : ''}

    ${sesion.consejos_seguridad && sesion.consejos_seguridad.length > 0 ? `
    <div class="hv-card hv-card-green" style="margin-top: 25px;">
      <h4 style="color: #62B882; margin-bottom: 15px;">üí° Consejos de seguridad</h4>
      <ul style="color: #B5B5B5; padding-left: 20px;">
        ${sesion.consejos_seguridad.map(c => `<li>${c}</li>`).join('')}
      </ul>
    </div>
    ` : ''}

    ${sesion.progresion_sugerida ? `
    <div class="hv-card" style="margin-top: 25px;">
      <h4 style="color: #DFCA61; margin-bottom: 15px;">üìà Progresi√≥n sugerida</h4>
      <p style="color: #B5B5B5;">${sesion.progresion_sugerida}</p>
    </div>
    ` : ''}
  `;

} else {
  // ========== SESI√ìN FUERZA ==========
  console.log('üí™ Generando HTML para sesi√≥n FUERZA');

  // Parsear JSONs si vienen como strings
  const calentamiento = typeof sesion.calentamiento === 'string'
    ? JSON.parse(sesion.calentamiento)
    : sesion.calentamiento;

  const trabajoPrincipal = typeof sesion.trabajo_principal === 'string'
    ? JSON.parse(sesion.trabajo_principal)
    : sesion.trabajo_principal;

  if (!calentamiento || !trabajoPrincipal) {
    throw new Error('Datos de ejercicios no encontrados para sesi√≥n de fuerza');
  }

  console.log(`üèãÔ∏è ${calentamiento.length} ejercicios de calentamiento`);
  console.log(`üí™ ${trabajoPrincipal.length} ejercicios principales`);

  // Funci√≥n para generar HTML de un ejercicio
  function generarEjercicioHTML(ejercicio, index, tipo) {
    const videoUrl = `${firebaseBaseUrl}${encodeURIComponent(ejercicio.nombre_archivo)}${firebaseToken}`;
    const numero = tipo === 'calentamiento' ? `C${index + 1}` : index + 1;

    return `
      <div class="ejercicio ejercicio-${tipo}">
        <div class="ejercicio-header">
          <div class="ejercicio-numero ejercicio-numero-${tipo}">${numero}</div>
          <h3>${ejercicio.nombre_espanol}</h3>
        </div>

        <div class="video-container">
          <video controls playsinline loop preload="metadata">
            <source src="${videoUrl}" type="video/quicktime">
            <source src="${videoUrl}" type="video/mp4">
            Tu navegador no soporta el tag de video.
          </video>
        </div>

        <div class="ejercicio-detalles">
          <div><strong>üìä Repeticiones:</strong> ${ejercicio.repeticiones}</div>
          <div><strong>‚è±Ô∏è Duraci√≥n:</strong> ${ejercicio.duracion_aprox}</div>
          ${ejercicio.notas ? `
          <div class="ejercicio-consejo">
            <strong>üí° Consejo:</strong> ${ejercicio.notas}
          </div>
          ` : ''}
        </div>
      </div>
    `;
  }

  // Generar HTML de ejercicios
  htmlCalentamiento = calentamiento.map((ej, idx) =>
    generarEjercicioHTML(ej, idx, 'calentamiento')
  ).join('');

  htmlTrabajoPrincipal = trabajoPrincipal.map((ej, idx) =>
    generarEjercicioHTML(ej, idx, 'principal')
  ).join('');
}

// Iconos por enfoque
const iconosEnfoque = {
  'movilidad': 'üßò',
  'fuerza': 'üí™',
  'cardio': '‚ù§Ô∏è',
  'equilibrio': '‚öñÔ∏è',
  'mixto': 'üéØ'
};
const iconoEnfoque = iconosEnfoque[sesion.enfoque] || 'üéØ';

// Reemplazar variables en el template
let paginaHTML = templatePagina
  .replace(/\{\{css_base\}\}/g, cssBase)
  .replace(/\{\{titulo\}\}/g, sesion.titulo)
  .replace(/\{\{user_nombre\}\}/g, sesion.user_nombre)
  .replace(/\{\{duracion_estimada\}\}/g, sesion.duracion_estimada)
  .replace(/\{\{nivel_actual\}\}/g, sesion.nivel_actual)
  .replace(/\{\{enfoque\}\}/g, sesion.enfoque)
  .replace(/\{\{numero_sesion\}\}/g, sesion.numero_sesion)
  .replace(/\{\{sesiones_objetivo\}\}/g, sesion.sesiones_objetivo_semana)
  .replace(/\{\{introduccion_personalizada\}\}/g, sesion.introduccion_personalizada)
  .replace(/\{\{html_calentamiento\}\}/g, htmlCalentamiento)
  .replace(/\{\{html_trabajo_principal\}\}/g, htmlTrabajoPrincipal)
  .replace(/\{\{webhook_url\}\}/g, webhookUrl)
  .replace(/\{\{user_id\}\}/g, sesion.user_id)
  .replace(/\{\{feedback_problemas_url\}\}/g, feedbackProblemasUrl)
  .replace(/\{\{icono_enfoque\}\}/g, iconoEnfoque);

console.log('‚úÖ HTML generado correctamente');

return {
  json: {
    sesion_id: sesion.id,
    html: paginaHTML
  }
};
